import React, { useMemo } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  Filler,
} from "chart.js";

import { Bar } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

const amountMale = [6, 5, 15, 12, 3, 4, 6, 4, 5];
const amountFemale = [16,11, 13, 25, 23,14, 9, 14, 5];
const labels = [100, 200, 300, 400, 500, 600, 700];

const options = {
  fill: true,
  animations: false,
  scales: {
    y: {
      min: 0,
    },
  },
  responsive: true,
  plugins: {
    legend: {
      display: true,
    },
    title: {
      display: true,
      text: 'Sales'
    }
  },

};

export default function BarChart() {
  const data = useMemo(function () {
    return {
      datasets: [
        {
          label: "Male",
          tension: 0.3,
          data: amountMale,
          borderColor: "rgb(75, 192, 192)",
          backgroundColor: "rgba(75, 192, 192, 0.5)",
        },
        {
          label: "female",
          tension: 0.3,
          data: amountFemale,
          borderColor: "rgb(175, 152, 192)",
          backgroundColor: "rgba(725, 132, 192, 0.5)",
        },
      ],
      labels,
    };
  }, []);

  return (
    <div className="App">

      <Bar data={data} options={options} />
    </div>
  );
}
