import { useMemo } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
  ArcElement,
} from "chart.js";
import { Pie } from "react-chartjs-2";

ChartJS.register(
  ArcElement,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

const supply = [2326,3435, 1245, 1452];

const labels = ["European", "America", "Asian","Australia", ];

const options = {
  fill: true,
  responsive: true,
  scales: {
    y: {
      min: 0,
    },
  },
  plugins: {
    legend: {
      display: true,
    },
    title: {
      display: true,
      text: 'Global Market Share 2022'
    },
    datalabels: {
      formatter: (value, categories) => {

          let sum = 0;
          let dataArr = categories.chart.data.datasets[0].data;
          dataArr.map(data => {
              sum += data;
          });
          let percentage = (value*100 / sum).toFixed(2)+"%";
          return percentage;


      },
      color: '#fff',
  }
  },
};

export default function PieChart() {
  const data = useMemo(function () {
    return {
      datasets: [
        {
          label: "Supply",
          data: supply,
          borderColor: "rgb(75, 192, 192)",
          backgroundColor: ["#C78283", "#F3D9DC", "#D7BEA8", "#744253"],
        },


      ],
      labels,
    };
  }, []);

  return <Pie data={data} options={options} />;
}

