import React, { useMemo } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  Filler,
} from "chart.js";

import { Bar } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

const departmentA = [226,345, 125, 142, 543, 324, 546, 124, 245];
const departmentB = [163,115, 213, 325, 423,154, 649, 734, 615];
const labels = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul","Aug","Sep","Oct","Nov","Dec"];

const options = {
  fill: true,
  animations: false,
  scales: {
    y: {
      min: 0,
    },
  },
  responsive: true,
  plugins: {
    legend: {
      display: true,
    },
    title: {
      display: true,
      text: 'Regional Sales 2022'
    }
  },

};

export default function BarChart() {
  const data = useMemo(function () {
    return {
      datasets: [
        {
          label: "Department B",
          tension: 0.3,
          data: departmentA,
          borderColor: "rgb(75, 192, 192)",
          backgroundColor: "rgba(75, 192, 192, 0.5)",
        },
        {
          label: "Department A ",
          tension: 0.3,
          data: departmentB,
          borderColor: "rgb(175, 152, 192)",
          backgroundColor: "rgba(725, 132, 192, 0.5)",
        },
      ],
      labels,
    };
  }, []);

  return (
    <div className="App">

      <Bar data={data} options={options} />
    </div>
  );
}
